DLG.Name="lost_trooper"DLG.RootNode=1 DLG.Nodes={{Type=1,SubNode=2,HasCondition=false,},{Type=2,SubNode=3,Text="Ha.  I must be hallucinating...you can't be real can you?",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=3,SubNode=4,NextNode=7,Text="I'm definitely real.",HasCondition=false,HasResolver=false,},{Type=2,SubNode=5,Text="That's just what a hallucination would say!",Duration=2e3,HasCondition=false,HasResolver=false,},{Type=2,SubNode=6,Text="I've got to find a way off this rock before I completely lose it...\nCome on Gary, they're not real.\nThe people AREN'T REAL!",Duration=5e3,HasCondition=false,HasResolver=false,},{Type=5,},{Type=3,SubNode=8,NextNode=11,Text="Just another Jedi mind trick I'm afraid...",HasCondition=false,HasResolver=false,},{Type=2,SubNode=9,Text="Of...course!  That's why it doesn't make any sense, there's a Jedi messing with my mind.",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=2,SubNode=10,Text="The Captain would have never left me alone here otherwise.\nGotta find that Jedi...",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=5,},{Type=3,SubNode=12,NextNode=21,Text="No, not at all.",HasCondition=false,HasResolver=false,},{Type=2,SubNode=13,Text="That's-wait...are you real?",Duration=2e3,HasCondition=false,HasResolver=false,},{Type=3,SubNode=14,NextNode=16,Text="[Force Persuade] I am.",HasCondition=false,HasResolver=false,},{Type=2,SubNode=15,Text="^1FOR THE EMPEROR!",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=5,},{Type=3,SubNode=17,NextNode=19,Text="[Persuade] If I shoot you, will the pain convince you?",HasCondition=false,HasResolver=false,},{Type=2,SubNode=18,Text="^1FOR THE EMPIRE!",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=5,},{Type=3,SubNode=20,Text="Nevermind...",HasCondition=false,HasResolver=false,},{Type=4,Target=5,},{Type=3,SubNode=22,Text="Snap out of it soldier and give me a report!",HasCondition=false,HasResolver=false,},{Type=2,SubNode=23,Text="Sir! The rebels are here they-wait, who are you?!",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=3,SubNode=24,NextNode=26,Text="A friend.",HasCondition=false,HasResolver=false,},{Type=2,SubNode=25,Text="I must be losing it.  A friend?  Out here?\nJ-ju-st hearing things...n-nothing real out here.",Duration=4e3,HasCondition=false,HasResolver=false,},{Type=4,Target=5,},{Type=3,SubNode=27,NextNode=29,Text="The last person you'll ever see.",HasCondition=false,HasResolver=false,},{Type=2,SubNode=28,Text="No...no they're not real!  NONE OF YOU ARE REAL!",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=4,Target=34,},{Type=3,SubNode=30,NextNode=32,Text="Your worst nightmare.",HasCondition=false,HasResolver=false,},{Type=2,SubNode=31,Text="I've seen worse.  At least you only have one mouth.  Gotta get some sleep...",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=5,},{Type=3,SubNode=33,Text="I'm you're commanding officer private!  Are you questioning me?",HasCondition=false,HasResolver=false,},{Type=2,SubNode=34,Text="No!  A trick of the enemy sent to confuse us.",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=2,SubNode=35,Text="IT WON'T WORK!  ^1IT WON'T WORK YOU HEAR ME!?",Duration=3e3,HasCondition=false,HasResolver=false,},{Type=5,},}